import React, { useEffect, useState } from "react";
import DogCard from "../dogCard/DogCard";
import Pagination from "../pagination/Pagination";
import Loading from "../loading/Loading";

const DogsList = ({ breeds, currentPage, breedsPerPage }) => {
  const indexOfLastBreed = currentPage * breedsPerPage;
  const indexOfFirstBreed = indexOfLastBreed - breedsPerPage;
  const currentBreeds = breeds.slice(indexOfFirstBreed, indexOfLastBreed);

  return (
    <div className="dog-list-container">
      {breeds.length > 0 ? (
        <div>
          <div className="dog-list">
            {currentBreeds.map((breed) => (
              <DogCard key={breed.id} breed={breed} />
            ))}
          </div>
          <Pagination
            breedsPerPage={breedsPerPage}
            totalBreeds={breeds.length}
          />
        </div>
      ) : (
        <p>No hay perros disponibles</p>
      )}
    </div>
  );
};

export default DogsList;
