import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getDogs, setPageNumber } from "../../redux/actions";
import DogCard from "../dogCard/DogCard";
import SearchBar from "../searchBar/SearchBar";
import Pagination from "../pagination/Pagination";
import FilterDropdown from "../filterDropDown/FilterDropDown";
import "./homePage.css";

const HomePage = ({ getDogs, setPageNumber, dogs, loading, error }) => {
  useEffect(() => {
    getDogs();
    setPageNumber(1);
  }, [getDogs, setPageNumber]);

  const renderDogs = () => {
    if (loading) {
      return <p>Loading...</p>;
    }

    if (error) {
      return <p>There was an error loading the dogs</p>;
    }

    return dogs.map((dog) => {
      return (
        <DogCard
          key={dog.id}
          id={dog.id}
          name={dog.name}
          image={dog.image}
          temperament={dog.temperament}
          weight={dog.weight.metric}
        />
      );
    });
  };

  return (
    <div className="homepage-container">
      <h1>Dog Breeds</h1>
      <SearchBar />
      <div className="filters-and-dogs-container">
        <div className="filters-container">
          <FilterDropdown />
        </div>
        <div className="dogs-container">{renderDogs()}</div>
      </div>
      <Pagination />
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    dogs: state.dogs.dogsList,
    loading: state.dogs.loading,
    error: state.dogs.error,
  };
};

const mapDispatchToProps = {
  getDogs,
  setPageNumber,
};

export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
