import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { changePage } from "../../redux/actions/index";
import "./pagination.css";

const Pagination = () => {
  const dispatch = useDispatch();

  const breedsPerPage = useSelector((state) => state.breedsPerPage);
  const breeds = useSelector((state) => state.breeds);

  const [pageNumbers, setPageNumbers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  const handleClick = (pageNumber) => {
    dispatch(changePage(pageNumber));
    setCurrentPage(pageNumber);
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      dispatch(changePage(currentPage - 1));
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < pageNumbers.length) {
      dispatch(changePage(currentPage + 1));
      setCurrentPage(currentPage + 1);
    }
  };

  useEffect(() => {
    setPageNumbers(() => {
      const totalPages = Math.ceil(breeds.length / breedsPerPage);
      if (totalPages <= 5) {
        return Array.from(Array(totalPages).keys());
      } else {
        const half = Math.floor(5 / 2);
        let start = Math.max(currentPage - half, 0);
        let end = start + 4;
        if (end >= totalPages) {
          end = totalPages - 1;
          start = Math.max(end - 4, 0);
        }
        return Array.from(Array(end - start + 1).keys()).map((n) => n + start);
      }
    });
  }, [breeds, breedsPerPage, currentPage]);

  return (
    <nav className="pagination-bar">
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 1}>
          {"<"}
        </button>
        {pageNumbers.map((number) => (
          <button
            key={number}
            onClick={() => handleClick(number + 1)}
            className={`page-button ${
              currentPage === number + 1 ? "current" : ""
            }`}
          >
            {number + 1}
          </button>
        ))}
        <button
          onClick={handleNextPage}
          disabled={currentPage === pageNumbers.length}
        >
          {">"}
        </button>
      </div>
    </nav>
  );
};

export default Pagination;
