import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { sortBreedsByName, sortBreedsByWeight } from "../../redux/actions";

const Sorting = () => {
  const dispatch = useDispatch();
  const sortOrder = useSelector((state) => state.sortOrder);

  const handleNameSort = (e) => {
    e.preventDefault();
    const order = sortOrder.name === "asc" ? "desc" : "asc";
    dispatch(sortBreedsByName(order));
  };

  const handleWeightSort = (e) => {
    e.preventDefault();
    const order = sortOrder.weight === "asc" ? "desc" : "asc";
    dispatch(sortBreedsByWeight(order));
  };

  return (
    <div className="sorting">
      <h3>Sort by:</h3>
      <div className="sorting-buttons">
        <button
          className={sortOrder.name === "asc" ? "active" : ""}
          onClick={handleNameSort}
        >
          Name {sortOrder.name === "asc" ? "▲" : "▼"}
        </button>
        <button
          className={sortOrder.weight === "asc" ? "active" : ""}
          onClick={handleWeightSort}
        >
          Weight {sortOrder.weight === "asc" ? "▲" : "▼"}
        </button>
      </div>
    </div>
  );
};

export default Sorting;
