import React from "react";
import { useSelector, useDispatch } from "react-redux";
import DogCard from "../dogCard/DogCard";
import Pagination from "../pagination/Pagination";

const DogsList = () => {
  const views = [
    {
      name: "All Dogs",
      data: useSelector((state) => state.breeds),
      show: true,
    },
    {
      name: "Filter by API",
      data: useSelector((state) => state.filteredByOrigin),
      show: true,
    },
    {
      name: "Filter by Temperament",
      data: useSelector((state) => state.filteredByTemperament),
      show: true,
    },
  ];

  const currentPage = useSelector((state) => state.currentPage);
  const breedsPerPage = useSelector((state) => state.breedsPerPage);

  const paginate = (breedsList) => {
    return breedsList.slice(
      (currentPage - 1) * breedsPerPage,
      currentPage * breedsPerPage
    );
  };

  return (
    <div className="dogslist">
      <div className="pagination">
        <Pagination />
      </div>

      {views.map((view) => {
        if (!view.show) return null;

        return (
          <div key={view.name} className={`dogcard${view.name}`}>
            {paginate(view.data).map((breed) => (
              <DogCard key={breed.id} breed={breed} />
            ))}
          </div>
        );
      })}
    </div>
  );
};

export default DogsList;
