import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  findBreeds,
  filterByTemperament,
  filterByOrigin,
} from "../../redux/actions";

const SearchBar = () => {
  const dispatch = useDispatch();
  const [searchTerm, setSearchTerm] = useState("");
  const [temperamentFilter, setTemperamentFilter] = useState("");
  const [originFilter, setOriginFilter] = useState("");

  const allTemperaments = useSelector((state) => state.allTemperaments);

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleTemperamentFilterChange = (e) => {
    setTemperamentFilter(e.target.value);
  };

  const handleOriginFilterChange = (e) => {
    setOriginFilter(e.target.value);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    dispatch(findBreeds(searchTerm.trim()));
    setSearchTerm("");
  };

  const handleFilter = (e) => {
    e.preventDefault();

    if (temperamentFilter) {
      dispatch(filterByTemperament(temperamentFilter));
    }

    if (originFilter) {
      dispatch(filterByOrigin(originFilter));
    }
  };

  const handleClearFilters = () => {
    setTemperamentFilter("");
    setOriginFilter("");
    dispatch(findBreeds(""));
    dispatch(filterByTemperament(""));
    dispatch(filterByOrigin(""));
  };

  useEffect(() => {
    dispatch(findBreeds(""));
    dispatch(filterByTemperament(""));
    dispatch(filterByOrigin(""));
  }, []);

  return (
    <div className="search-bar">
      <input
        className="input-search"
        type="text"
        placeholder="Search by breed name..."
        value={searchTerm}
        onChange={handleChange}
      />

      {searchTerm && (
        <button
          type="reset"
          className="clear-button"
          onClick={() => setSearchTerm("")}
        >
          x
        </button>
      )}

      <div className="filters">
        <select
          className="temperament-filter"
          value={temperamentFilter}
          onChange={handleTemperamentFilterChange}
        >
          <option value="">Filter by temperament</option>
          {allTemperaments.map((temperament) => (
            <option key={temperament} value={temperament}>
              {temperament}
            </option>
          ))}
        </select>

        <select
          className="origin-filter"
          value={originFilter}
          onChange={handleOriginFilterChange}
        >
          <option value="">Filter by origin</option>
          <option value="API">API</option>
          <option value="Database">Database</option>
        </select>

        <button className="apply-filters" onClick={handleFilter}>
          Apply
        </button>

        <button className="clear-filters" onClick={handleClearFilters}>
          Clear filters
        </button>
      </div>

      <button type="search" className="search-button" onClick={handleSearch}>
        Search
      </button>
    </div>
  );
};

export default SearchBar;
