import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from "react-redux";
import { loadDogs, filterDogs, sortDogs, changePage, searchDogsByName } from '../../redux/actions/index';
import SearchBar from '../searchBar/SearchBar';
import DogCard from '../dogCard/DogCard';
import Filters from '../filters/Filters';
import Pagination from '../pagination/Pagination';
import "./homePage.css"


const HomePage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [filterOptions, setFilterOptions] = useState({ temperament: "", origin: "" });
  const [sortType, setSortType] = useState("");

  const dispatch = useDispatch();

  const dogs = useSelector((state) => state.dogs);
  const currentPage = useSelector((state) => state.currentPage);
  const dogsPerPage = 8;

  useEffect(() => {
    dispatch(loadDogs());
  }, [dispatch]);

  const handleSearch = (event) => {
    event.preventDefault();
    dispatch(searchDogsByName(searchTerm));
  };

  const handleFilter = (event) => {
    event.preventDefault();
    dispatch(filterDogs(filterOptions));
  };

  const handleSort = (event) => {
    event.preventDefault();
    dispatch(sortDogs(sortType));
  };

  const handlePageChange = (pageNumber) => {
    dispatch(changePage(pageNumber));
  };

  const indexOfLastDog = currentPage * dogsPerPage;
  const indexOfFirstDog = indexOfLastDog - dogsPerPage;
  const currentDogs = Array.isArray(dogs) ? dogs.slice(indexOfFirstDog, indexOfLastDog) : [];

  return (
    <div className="filters-and-search">
      <h1>List of Dogs</h1>
      <SearchBar searchTerm={searchTerm} setSearchTerm={setSearchTerm} handleSearch={handleSearch} />
      <Filters filterOptions={filterOptions} setFilterOptions={setFilterOptions} handleFilter={handleFilter} handleSort={handleSort} />
      <div className="card-container">
        {currentDogs.map((dog) => (
          <DogCard key={dog.id} dog={dog} />
        ))}
      </div>
      <Pagination
        currentPage={currentPage}
        dogsPerPage={dogsPerPage}
        totalDogs={dogs.length}
        handlePageChange={handlePageChange}
      />
    </div>
  );
};

export default HomePage;
