import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { findBreedsByName } from "../../redux/actions";

const SearchBar = () => {
  const dispatch = useDispatch();
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResult, setSearchResult] = useState(true);

  useEffect(() => {
    dispatch(findBreedsByName()).then(() => setSearchResult(false));
  }, [dispatch]);

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    const result = dispatch(findBreedsByName(searchTerm.trim()));
    if (result.length === 0) {
      setSearchResult(true);
    } else {
      setSearchResult(false);
    }
    setSearchTerm("");
  };

  const handleClear = () => {
    setSearchTerm("");
  };

  return (
    <div className="searchBar-container">
      <form className="form-search" onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Search by name..."
          value={searchTerm}
          onChange={handleChange}
        />
        {searchTerm && (
          <button type="reset" className="clear-button" onClick={handleClear}>
            x
          </button>
        )}
        <button type="submit" className="search-button">
          Search
        </button>
      </form>
      <div>{searchResult && <h2>Sin resultados</h2>}</div>
    </div>
  );
};

export default SearchBar;
