import {
  SEARCH_DOGS_BY_NAME,
  LOAD_DOGS,
  FILTER_DOGS,
  SORT_DOGS,
  GET_DOG_DETAIL,
  CHANGE_PAGE,
} from "../actions/index";

const initialState = {
  dogs: [],
  filteredDogs: [],
  dogDetail: null,
  currentPage: 1,
  dogsPerPage: 8,
};

const dogsReducer = (state = initialState, action) => {
  switch (action.type) {
    case SEARCH_DOGS_BY_NAME:
      return {
        ...state,
        filteredDogs: Array.isArray(action.payload) ? action.payload : [],
        currentPage: 1,
      };
    case LOAD_DOGS:
      return {
        ...state,
        dogs: action.payload,
        filteredDogs: action.payload,
      };
    case FILTER_DOGS:
      return {
        ...state,
        filteredDogs: Array.isArray(action.payload) ? action.payload : [],
        currentPage: 1,
      };
    case SORT_DOGS:
      return {
        ...state,
        filteredDogs: Array.isArray(action.payload) ? action.payload : [],
        currentPage: 1,
      };
    case GET_DOG_DETAIL:
      return {
        ...state,
        dogDetail: action.payload,
      };
    case CHANGE_PAGE:
      return {
        ...state,
        currentPage: action.payload,
      };
    default:
      return state;
  }
};

export default dogsReducer;
