import React from "react";
import PropTypes from 'prop-types';
import { useDispatch } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { getDogDetail } from '../../redux/actions/index';
import "./dogCard.css";

const DogCard = ({ dog }) => {
    const dispatch = useDispatch();
    const history = useHistory();

    const handleDogClick = () => {
      dispatch(getDogDetail(dog.id));
      history.push(`/dogs/${dog.id}`);
    };

    return (
      <div className="dog-card">
        <img src={dog.image} alt={dog.name} className="dog-card__image" />
        <div className="dog-card__info">
          <h3 className="dog-card__name">{dog.name}</h3>
          <p className="dog-card__temperaments">{dog.temperaments.join(', ')}</p>
          <p className="dog-card__weight">Weight: {dog.weight}</p>
          <button className="dog-card__button" onClick={handleDogClick}>
            View Details
          </button>
        </div>
      </div>
    );
  };

  DogCard.propTypes = {
    dog: PropTypes.shape({
      id: PropTypes.number.isRequired,
      name: PropTypes.string.isRequired,
      image: PropTypes.string.isRequired,
      temperaments: PropTypes.arrayOf(PropTypes.string).isRequired,
      weight: PropTypes.string.isRequired,
    }).isRequired,
  };

  export default DogCard;
