import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { sortBreedsByName, sortBreedsByWeight } from "../../redux/actions";

const Sorting = () => {
  const dispatch = useDispatch();
  const [selectedOption, setSelectedOption] = useState("");

  const handleSortByOption = (option) => {
    option.preventDefault();
    setSelectedOption(option);

    if (option === "name") {
      if (selectedOption === "asc") {
        dispatch(sortBreedsByName("desc"));
        setSelectedOption("desc");
      } else {
        dispatch(sortBreedsByName("asc"));
        setSelectedOption("asc");
      }
    } else if (option === "weight") {
      if (selectedOption === "asc") {
        dispatch(sortBreedsByWeight("desc"));
        setSelectedOption("desc");
      } else {
        dispatch(sortBreedsByWeight("asc"));
        setSelectedOption("asc");
      }
    }
  };

  return (
    <div className="sorting-container">
      <label className="sorting-label">Sort by:</label>
      <select
        className="sorting-select"
        value={selectedOption}
        onChange={(e) => handleSortByOption(e.target.value)}
      >
        <option value="">--Sort by--</option>
        <option value="name">Name</option>
        <option value="weight">Weight</option>
      </select>
    </div>
  );
};

export default Sorting;
