import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { setSort, sortDogs } from "../../redux/actions/index";

const SortOptions = () => {
  const dispatch = useDispatch();
  const sort = useSelector((state) => state.dogs.sort);

  const handleSortChange = (e) => {
    const newSort = e.target.value;
    dispatch(setSort(newSort));
    dispatch(sortDogs());
  };

  return (
    <div>
      <label htmlFor="sort">Sort by:</label>
      <select id="sort" value={sort} onChange={handleSortChange}>
        <option value="name-asc">Name (A-Z)</option>
        <option value="name-desc">Name (Z-A)</option>
        <option value="weight-asc">Weight (Lowest to Highest)</option>
        <option value="weight-desc">Weight (Highest to Lowest)</option>
      </select>
    </div>
  );
};

export default SortOptions;
