// Action Types
export const FIND_BREEDS_BY_NAME = "FIND_BREEDS_BY_NAME";
export const GET_ALL_BREEDS = "GET_ALL_BREEDS";

import axios from "axios";

// acción para encontrar razas de perro por su nombre (que coincidan con el término de búsqueda)
export const findBreedsByName = (name) => async (dispatch) => {
  try {
    const res = await axios.get(`/dogs?name=${name}`);
    dispatch({ type: FIND_BREEDS_BY_NAME, payload: res.data });
  } catch (error) {
    console.error(error);
  }
};

// acción para obtener toda la lista de razas de perros
export const getAllBreeds = () => async (dispatch) => {
  try {
    const res = await axios.get("/dogs");
    dispatch({ type: GET_ALL_BREEDS, payload: res.data });
  } catch (error) {
    console.error(error);
  }
};

// acción para obtener todos los temperamentos de las razas de perros
export const GET_ALL_TEMPERAMENTS = "GET_ALL_TEMPERAMENTS";
export const getAllTemperaments = () => async (dispatch) => {
  try {
    const res = await axios.get("/temperaments");
    dispatch({ type: GET_ALL_TEMPERAMENTS, payload: res.data });
  } catch (error) {
    console.error(error);
  }
};

// acción para filtrar los perros por temperamento
export const FILTER_BY_TEMPERAMENT = "FILTER_BY_TEMPERAMENT";
export const filterByTemperament = (temperament) => async (dispatch) => {
  try {
    const res = await axios.get(`/dogs?temperament=${temperament}`);
    dispatch({ type: FILTER_BY_TEMPERAMENT, payload: res.data });
  } catch (error) {
    console.error(error);
  }
};

// acción para filtrar por origen, ya sea obtener los perros de la API externa u obtener los perros de la base de datos interna
export const FILTER_BY_ORIGIN = "FILTER_BY_ORIGIN";
export const filterByOrigin = (origin) => async (dispatch) => {
  try {
    const res = await axios.get(`/dogs?origin=${origin}`);
    dispatch({ type: FILTER_BY_ORIGIN, payload: res.data });
  } catch (error) {
    console.error(error);
  }
};
