import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { findBreedsByName } from "../../redux/actions";

const SearchBar = () => {
  const dispatch = useDispatch();
  const [searchTerm, setSearchTerm] = useState("");
  const [breedNotFound, setBreedNotFound] = useState(false);

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
    setBreedNotFound(false);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    dispatch(findBreedsByName(searchTerm.trim())).then((response) => {
      if (response === false) {
        setBreedNotFound(true); // Actualizar la variable de estado si no se encontr贸 ninguna raza de perro
      }
    });
    setSearchTerm("");
  };

  const handleClear = () => {
    setSearchTerm("");
  };

  return (
    <div className="searchBar-container">
      <form className="form-search" onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Search by name..."
          value={searchTerm}
          onChange={handleChange}
        />
        {searchTerm && (
          <button type="reset" className="clear-button" onClick={handleClear}>
            x
          </button>
        )}
        <button type="submit" className="search-button">
          Search
        </button>
      </form>
      {breedNotFound && (
        <h2>No se encontro ninguna raza de perro con ese nombre</h2> // Mostrar el mensaje si no se encontr贸 ninguna raza de perro
      )}
    </div>
  );
};

export default SearchBar;
