import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getDogs, getTemperaments, filterByName, filterByTemperament, filterByOrigin, sortByName, sortByWeight, setPageNumber } from "../../redux/actions";
import DogCard from "../components/DogCard";
import SearchBar from "../components/SearchBar";
import FilterSelect from "../components/FilterSelect";
import OrderSelect from "../components/OrderSelect";
import Pagination from "../components/Pagination";
import "./homePage.css";

const HomePage = () => {
  const dispatch = useDispatch();
  const dogs = useSelector((state) => state.dogs);
  const temperaments = useSelector((state) => state.temperaments);
  const filters = useSelector((state) => state.filters);
  const pageNumber = useSelector((state) => state.pageNumber);

  useEffect(() => {
    dispatch(getDogs());
    dispatch(getTemperaments());
  }, [dispatch]);

  const handleFilterByName = (e) => {
    dispatch(filterByName(e.target.value));
    dispatch(setPageNumber(1));
  };

  const handleFilterByTemperament = (e) => {
    dispatch(filterByTemperament(e.target.value));
    dispatch(setPageNumber(1));
  };

  const handleFilterByOrigin = (e) => {
    dispatch(filterByOrigin(e.target.value));
    dispatch(setPageNumber(1));
  };

  const handleSortByName = (e) => {
    dispatch(sortByName(e.target.value));
    dispatch(setPageNumber(1));
  };

  const handleSortByWeight = (e) => {
    dispatch(sortByWeight(e.target.value));
    dispatch(setPageNumber(1));
  };

  const handleSetPageNumber = (page) => {
    dispatch(setPageNumber(page));
  };

  return (
    <div>
      <Filters
        temperaments={temperaments}
        handleFilterByName={handleFilterByName}
        handleFilterByTemperament={handleFilterByTemperament}
        handleFilterByOrigin={handleFilterByOrigin}
        handleSortByName={handleSortByName}
        handleSortByWeight={handleSortByWeight}
      />
      <Dogs dogs={dogs} filters={filters} pageNumber={pageNumber} />
      <Pagination dogs={dogs} handleSetPageNumber={handleSetPageNumber} pageNumber={pageNumber} />
    </div>
  );
};

export default HomePage;
