import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { loadDogs, changePage } from '../../redux/actions/index';
import DogCard from '../dogCard/DogCard';
import Pagination from '../pagination/Pagination';

const DogsList = () => {
  const dispatch = useDispatch();
  const dogs = useSelector(state => state.dogs);
  const currentPage = useSelector(state => state.dogs.currentPage);

  const dogsPerPage = 8;

  useEffect(() => {
    dispatch(loadDogs());
  }, [dispatch]);

  const handleClick = page => {
    dispatch(changePage(page));
  };

  let currentDogs = [];

  if (Array.isArray(dogs)) {
    const indexOfLastDog = currentPage * dogsPerPage;
    const indexOfFirstDog = indexOfLastDog - dogsPerPage;
    currentDogs = dogs.slice(indexOfFirstDog, indexOfLastDog);
  }

  return (
    <div className="dogs-list">
      {currentDogs.map(dog => (
        <DogCard key={dog.id} dog={dog} />
      ))}
      <Pagination
        itemsPerPage={dogsPerPage}
        totalItems={dogs?.length}
        currentPage={currentPage}
        handleClick={handleClick}
      />
    </div>
  );
};

export default DogsList;
