import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getAllBreeds, getAllTemperaments, changePage, clearFilters, clearSearch } from '../../redux/actions/index';
import SearchBar from '../searchBar/SearchBar';
import Header from '../header/Header';
import DogsList from '../dogsList/DogsList';
import Filters from '../filters/Filters';
import Sorting from '../sorting/Sorting';
import Pagination from '../pagination/Pagination';

const HomePage = () => {
  const dispatch = useDispatch();
  const breeds = useSelector((state) => state.allBreeds);
  const temperaments = useSelector((state) => state.allTemperaments);
  const currentPage = useSelector((state) => state.currentPage);
  const breedsPerPage = useSelector((state) => state.breedsPerPage);
  const filteredBreeds = useSelector((state) => state.filteredBreeds);
  const searchedBreeds = useSelector((state) => state.searchResults);

  useEffect(() => {
    dispatch(getAllBreeds());
    dispatch(getAllTemperaments());
    dispatch(changePage(1));
    dispatch(clearFilters());
    dispatch(clearSearch());
  }, [dispatch]);

  return (
    <div>
        {loading ? (
        <Loading />
      ) : breed ? (
    <div>
      <Header />
      <SearchBar />
      <div className="main-container">
        <div className="filters-container">
          <Filters temperaments={temperaments} />
          <Sorting />
        </div>
        <div className="dogs-container">
          <DogsList
            breeds={ searchedBreeds.length > 0 ? searchedBreeds : filteredBreeds.length > 0 ? filteredBreeds : breeds}
            currentPage={currentPage}
            breedsPerPage={breedsPerPage}
          />
          {/* <Pagination totalBreeds={searchedBreeds.length > 0 ? searchedBreeds.length : filteredBreeds.length > 0 ? filteredBreeds.length : breeds.length} /> */}
        </div>
      </div>
    </div>
      ) 
      : (
        <h2>Breed not found</h2>
      )
      }
    </div>
  );
};

export default HomePage;
