import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import DogCard from "../dogCard/DogCard";
import Pagination from "../pagination/Pagination";

const DogsList = ({ breeds, currentPage, breedsPerPage }) => {
  const dispatch = useDispatch();
  const indexOfLastBreed = currentPage * breedsPerPage;
  const indexOfFirstBreed = indexOfLastBreed - breedsPerPage;
  const currentBreeds = breeds.slice(indexOfFirstBreed, indexOfLastBreed);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    dispatch(setLoading(false));
  }, [dispatch]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!loading && currentBreeds.length === 0) {
    return <p>No se encontró ningún perro con el nombre ingresado.</p>;
  }

  return (
    <div className="dogslist-container">
      <div className="dogcard-container">
        {currentBreeds.map((breed) => (
          <DogCard key={breed.id} breed={breed} />
        ))}
      </div>
      {/* {currentBreeds.length === 0 && (
        <h2>No se encontró ningún perro con el nombre ingresado</h2>
      )} */}
      <div className="pagination">
        <Pagination
          breedsPerPage={breedsPerPage}
          totalBreeds={breeds.length}
          currentPage={currentPage}
        />
      </div>
    </div>
  );
};

export default DogsList;
