import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  getAllBreeds,
  getAllTemperaments,
  changePage,
  clearFilters,
  clearSearch,
} from "../../redux/actions/index";
import SearchBar from "../searchBar/SearchBar";
import Header from "../header/Header";
import DogsList from "../dogsList/DogsList";
import Filters from "../filters/Filters";
import Sorting from "../sorting/Sorting";
import Loading from "../loading/Loading";
import Pagination from "../pagination/Pagination";

const HomePage = () => {
  const dispatch = useDispatch();
  const breeds = useSelector((state) => state.allBreeds);
  const temperaments = useSelector((state) => state.allTemperaments);
  const currentPage = useSelector((state) => state.currentPage);
  const breedsPerPage = useSelector((state) => state.breedsPerPage);
  const filteredBreeds = useSelector((state) => state.filteredBreeds);
  const searchedBreeds = useSelector((state) => state.searchResults);
  const loading = useSelector((state) => state.loading);

  useEffect(() => {
    dispatch(getAllBreeds());
    dispatch(getAllTemperaments());
    dispatch(changePage(1));
    dispatch(clearFilters());
    dispatch(clearSearch());
  }, [dispatch]);

  return (
    <div>
      {loading ? (
        <Loading />
      ) : (
        <div className="home-page">
          <Header />
          <SearchBar />
          <div className="filtersAndSorting-container">
            <div className="filtersAndSorting">
              <Filters temperaments={temperaments} />
              <Sorting />
            </div>
          </div>
          {searchedBreeds.length > 0 ? (
            <DogsList
              breeds={searchedBreeds}
              currentPage={currentPage}
              breedsPerPage={breedsPerPage}
            />
          ) : filteredBreeds.length > 0 ? (
            <DogsList
              breeds={filteredBreeds}
              currentPage={currentPage}
              breedsPerPage={breedsPerPage}
            />
          ) : breeds.length > 0 ? (
            <DogsList
              breeds={breeds}
              currentPage={currentPage}
              breedsPerPage={breedsPerPage}
            />
          ) : (
            <div>No se encontraron razas de perros</div>
          )}
          {searchedBreeds.length > 0 ||
          filteredBreeds.length > 0 ||
          breeds.length > 0 ? (
            <Pagination
              totalBreeds={
                searchedBreeds.length > 0
                  ? searchedBreeds.length
                  : filteredBreeds.length > 0
                  ? filteredBreeds.length
                  : breeds.length
              }
              breedsPerPage={breedsPerPage}
            />
          ) : null}
        </div>
      )}
    </div>
  );
};

export default HomePage;
