import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { filterByOrigin, filterByTemperament } from '../../redux/actions';

const Filters = () => {
  const dispatch = useDispatch();
  const [originFilter, setOriginFilter] = useState('');
  const [temperamentFilter, setTemperamentFilter] = useState('');

  const handleOriginFilterChange = (event) => {
    setOriginFilter(event.target.value);
    dispatch(filterByOrigin(event.target.value));
  };

  const handleTemperamentFilterChange = (event) => {
    setTemperamentFilter(event.target.value);
    dispatch(filterByTemperament(event.target.value));
  };

  return (
    <div className="filters-container">
      <label htmlFor="origin-filter">Origin:</label>
      <select
        id="origin-filter"
        value={originFilter}
        onChange={handleOriginFilterChange}
      >
        <option value="">All</option>
        <option value="api">API</option>
        <option value="db">DB</option>
      </select>

      <label htmlFor="temperament-filter">Temperament:</label>
      <select
        id="temperament-filter"
        value={temperamentFilter}
        onChange={handleTemperamentFilterChange}
      >
        <option value="">All</option>
        <option value="Affectionate">Affectionate</option>
        <option value="Agile">Agile</option>
        <option value="Alert">Alert</option>
        <option value="Clever">Clever</option>
        <option value="Friendly">Friendly</option>
        <option value="Intelligent">Intelligent</option>
        <option value="Loyal">Loyal</option>
        <option value="Playful">Playful</option>
        <option value="Protective">Protective</option>
        <option value="Quiet">Quiet</option>
        <option value="Trainable">Trainable</option>
      </select>
    </div>
  );
};

export default Filters;
