import React from 'react';
import { useDispatch } from 'react-redux';
import { sortDogs } from '../../redux/actions/index';
import './sortBy.css';

const SortBy = () => {
  const dispatch = useDispatch();

  const handleSortOrderChange = (event) => {
    dispatch(sortDogs(event.target.value));
  };

  return (
    <div className="sort-by-container">
      <label className="sort-by-label" htmlFor="sort-by-select">
        Sort by:
      </label>
      <select className="sort-by-select" id="sort-by-select" onChange={handleSortOrderChange}>
        <option value="name-asc">Name (A-Z)</option>
        <option value="name-desc">Name (Z-A)</option>
        <option value="weight-asc">Weight (Low to High)</option>
        <option value="weight-desc">Weight (High to Low)</option>
      </select>
    </div>
  );
};

export default SortBy;
