import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { changePage } from '../../redux/actions/index';
import DogsList from '../dogsList/DogsList';

const Pagination = () => {
  const dispatch = useDispatch();
  const currentPage = useSelector(state => state.dogs.currentPage);
  const totalPages = useSelector(state => state.dogs.totalPages);
  const dogsPerPage = useSelector(state => state.dogs.dogsPerPage);
  const filteredDogs = useSelector(state => state.dogs.filteredDogs);

  const handleClick = (page) => {
    dispatch(changePage(page));
  };

  const createPageButtons = () => {
    let buttons = [];
    for (let i = 1; i <= totalPages; i++) {
      buttons.push(
        <button key={i} onClick={() => handleClick(i)} disabled={i === currentPage}>
          {i}
        </button>
      );
    }
    return buttons;
  };

  return (
    <div className="pagination-container">
      <ul className="pagination">
        {buttons.map((number) => (
          <li key={number} className="page-item">
            <button id={number} onClick={handleClick}>
              {number}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Pagination;
