import React from 'react';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { getDogDetail } from '../../redux/actions/index';

function DogCard({ id, name, image, temperament, weight }) {
  const dispatch = useDispatch();

  const handleClick = () => {
    dispatch(getDogDetail(id));
  }

  return (
    <div className="dog-card" onClick={handleClick}>
      <Link to={`/dogs/${id}`}>
        <img className="dog-card-image" src={image} alt={name} />
        <div className="dog-card-info">
          <h2 className="dog-card-name">{name}</h2>
          <p className="dog-card-temperament">{temperament}</p>
          <p className="dog-card-weight">{weight}</p>
        </div>
      </Link>
    </div>
  );
}

export default DogCard;
