import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { getDogs } from "../../redux/actions";
import DogCard from "../dogCard/DogCard";
import FilterDropdown from "../filterDropDown/FilterDropDown";
import Pagination from "../pagination/Pagination";
import SearchBar from "../searchBar/SearchBar";
import "./homePage.css"

const HomePage = () => {
  const dispatch = useDispatch();

  // Fetch dogs data from redux store
  const { dogs, totalDogs, currentPage, dogsPerPage, error } = useSelector(
    (state) => state
  );

  useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  const handleFilterByTemperament = (e) => {
    dispatch(setTemperamentFilter(e.target.value));
  };

  const handleFilterByOrigin = (e) => {
    dispatch(setOriginFilter(e.target.value));
  };

  const handleSortBy = (e) => {
    dispatch(setSortBy(e.target.value));
  };

  const handleOrderBy = (e) => {
    dispatch(setOrderBy(e.target.value));
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-10 col-lg-8">
          <SearchBar />
          <div className="d-flex justify-content-between align-items-center my-3">
            <h2>Dogs</h2>
            <div>
              <FilterDropdown
                title="Filter by temperament"
                options={["All", ...temperaments]}
                onChange={handleFilterByTemperament}
              />
              <FilterDropdown
                title="Filter by origin"
                options={["All", "API", "DB"]}
                onChange={handleFilterByOrigin}
              />
              <FilterDropdown
                title="Sort by"
                options={["Name", "Weight"]}
                onChange={handleSortBy}
              />
              <FilterDropdown
                title="Order by"
                options={["Ascending", "Descending"]}
                onChange={handleOrderBy}
              />
            </div>
          </div>
          <div className="row">
            {error ? (
              <div className="col-12 text-center">
                <h3>{error}</h3>
              </div>
            ) : (
              dogs.map((dog) => (
                <div key={dog.id} className="col-md-6 col-lg-4 my-3">
                  <Link to={`/dogs/${dog.id}`}>
                    <DogCard {...dog} />
                  </Link>
                </div>
              ))
            )}
          </div>
          <Pagination
            currentPage={currentPage}
            dogsPerPage={dogsPerPage}
            totalDogs={totalDogs}
          />
        </div>
      </div>
    </div>
  );
};

export default HomePage;
