import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { setSort } from '../redux/actions';
import "./sortOptions.css"

const SortOptions = () => {
  const dispatch = useDispatch();
  const [origin, setOrigin] = useState('');

  const handleSortChange = (event) => {
    const sortType = event.target.value;
    dispatch(setSort({ sortType, origin }));
  };

  const handleOriginChange = (event) => {
    const selectedOrigin = event.target.value;
    setOrigin(selectedOrigin);
    dispatch(setSort({ sortType: '', origin: selectedOrigin }));
  };

  return (
    <div className="sort-options">
      <label htmlFor="sort-select">Ordenar por:</label>
      <select id="sort-select" onChange={handleSortChange}>
        <option value="">Seleccione una opci√≥n</option>
        <option value="name_asc">Nombre ascendente</option>
        <option value="name_desc">Nombre descendente</option>
        <option value="weight_asc">Peso ascendente</option>
        <option value="weight_desc">Peso descendente</option>
      </select>
      <label htmlFor="origin-select">Origen:</label>
      <select id="origin-select" onChange={handleOriginChange}>
        <option value="">Todos</option>
        <option value="api">API</option>
        <option value="database">Base de datos</option>
      </select>
    </div>
  );
};

export default SortOptions;
