import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  findBreeds,
  getAllTemperaments,
  filterByOrigin,
  clearFilters,
  clearSearch,
} from "../../redux/actions/index";
import SearchBar from "../searchBar/SearchBar";
import Header from "../header/Header";
import DogsList from "../dogsList/DogsList";
import Filters from "../filters/Filters";
import Sorting from "../sorting/Sorting";
import Loading from "../loading/Loading";

const HomePage = () => {
  const dispatch = useDispatch();
  const [order, setOrder] = useState("");
  const [loading, setLoading] = useState(true);
  const breeds = useSelector((state) => state.breeds);
  const temperaments = useSelector((state) => state.allTemperaments);
  const currentPage = useSelector((state) => state.currentPage);
  const breedsPerPage = useSelector((state) => state.breedsPerPage);
  const filterOrigin = useSelector((state) => state.filteredByOrigin);
  const filterTemperament = useSelector((state) => state.filteredByTemperament);

  useEffect(() => {
    dispatch(findBreeds()).then(() => setLoading(false));
    dispatch(getAllTemperaments());
    dispatch(clearFilters());
    dispatch(clearSearch());
  }, []);

  useEffect(() => {
    dispatch(filterByOrigin());
  }, [currentPage, breedsPerPage, order]);

  const handleOrderChange = (event) => {
    setOrder(event.target.value);
  };

  return (
    <div className="home-page">
      <div className="header">
        <Header />
      </div>

      <div className="search-bar">
        <SearchBar />
      </div>

      <div className="filtersAndSorting">
        <Filters temperaments={temperaments} />
        <Sorting order={order} handleOrderChange={handleOrderChange} />
      </div>

      <div className="dogs-list">
        <div className="loading">{loading && <Loading />}</div>

        <div className="dog-notfound">
          {!loading && breeds.length === 0 && (
            <h2>No dog found with the entered name</h2>
          )}
        </div>

        {!loading && breeds.length > 0 && (
          <DogsList
            filterOrigin={filterOrigin}
            filterTemperament={filterTemperament}
          />
        )}
      </div>
    </div>
  );
};

export default HomePage;
