import axios from "axios";
import {
  findDogsApi,
  getDogApiTemperaments,
} from "/Users/andy/Desktop/Soy Henry || Full Stack/PROYECTO INDIVIDUAL/PI-SH-DOGS/api/src/controllers/dogsControllers";

export const GET_DOGS = "GET_DOGS";
export const GET_TEMPERAMENTS = "GET_TEMPERAMENTS";
export const FILTER_BY_NAME = "FILTER_BY_NAME";
export const FILTER_BY_TEMPERAMENT = "FILTER_BY_TEMPERAMENT";
export const FILTER_BY_ORIGIN = "FILTER_BY_ORIGIN";
export const SORT_BY_NAME = "SORT_BY_NAME";
export const SORT_BY_WEIGHT = "SORT_BY_WEIGHT";
export const SET_PAGE_NUMBER = "SET_PAGE_NUMBER";

export const getDogs = () => async (dispatch) => {
  try {
    const response = await axios.get("http://localhost:3001/dogs");
    const data = response.data.map((dog) => {
      const { id, name, weight, height, life_span, image, temperament } = dog;
      return {
        id,
        name,
        weight: weight.metric,
        height: height.metric,
        life_span,
        image,
        temperament: temperament?.split(","),
        createdInDb: false,
      };
    });
    dispatch({ type: GET_DOGS, payload: data });
  } catch (error) {
    console.log(error);
  }
};

export const getTemperaments = () => {
  return async (dispatch) => {
    try {
      const response = await axios.get("http://localhost:3001/temperament");
      const data = response.data;
      dispatch({
        type: GET_TEMPERAMENTS,
        payload: data,
      });
    } catch (error) {
      console.log(error);
    }
  };
};

export const filterByName = (name) => {
  return {
    type: FILTER_BY_NAME,
    payload: name,
  };
};

export const filterByTemperament = (temperament) => {
  return {
    type: FILTER_BY_TEMPERAMENT,
    payload: temperament,
  };
};

export const filterByOrigin = (origin) => {
  return {
    type: FILTER_BY_ORIGIN,
    payload: origin,
  };
};

export const sortByName = (sortType) => {
  return {
    type: SORT_BY_NAME,
    payload: sortType,
  };
};

export const sortByWeight = (sortType) => {
  return {
    type: SORT_BY_WEIGHT,
    payload: sortType,
  };
};

export const setPageNumber = (pageNumber) => {
  return {
    type: SET_PAGE_NUMBER,
    payload: pageNumber,
  };
};
