import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { loadDogs, changePage } from '../../redux/actions/index';
import { Link } from 'react-router-dom';
import Pagination from '../pagination/Pagination';

const DogsList = ({dogs}) => {
  const dispatch = useDispatch();
  const stateDogs = useSelector((state) => state.dogs);
  const { currentPage, dogsPerPage, filteredDogs } = stateDogs;

  useEffect(() => {
    dispatch(loadDogs());
  }, [dispatch]);

  const handleClick = (page) => {
    dispatch(changePage(page));
  };

  const indexOfLastDog = currentPage * dogsPerPage;
  const indexOfFirstDog = indexOfLastDog - dogsPerPage;
  const currentDogs = filteredDogs.slice(indexOfFirstDog, indexOfLastDog);


  return (
    <div className="dogs-list">
      {currentDogs.map((dog) => (
        <Link key={dog.id} to={`/dogs/${dog.id}`}>
          <div className="dog-card">
            <img src={dog.image.url} alt={dog.name} />
            <h3>{dog.name}</h3>
          </div>
        </Link>
      ))}
      <Pagination
        itemsPerPage={dogsPerPage}
        totalItems={filteredDogs.length}
        currentPage={currentPage}
        onPageChange={handleClick}
      />
    </div>
  );
};

export default DogsList;
