import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { loadDogs, searchDogsByName, filterDogs, sortDogs, setSort, changePage } from '../../redux/actions/index';
import DogsList from '../dogsList/DogsList';
import Filters from '../filters/Filters';
import Pagination from '../pagination/Pagination';
import SearchBar from '../searchBar/SearchBar';
import SortBy from '../sortBy/SortBy';
import SortOptions from '../sortOptions';

const HomePage = () => {
  const dispatch = useDispatch();
  const dogs = useSelector((state) => state.dogs);
  const { loading, error, filteredDogs, currentPage, dogsPerPage, searchValue, filterValue, sortValue } = dogs;

  useEffect(() => {
    dispatch(loadDogs());
  }, [dispatch]);

  const handleSearch = (searchValue) => {
    dispatch(searchDogsByName(searchValue));
  };

  const handleFilter = (filterValue) => {
    dispatch(filterDogs(filterValue));
  };

  const handleSort = (sortValue) => {
    dispatch(setSort(sortValue));
    dispatch(sortDogs(sortValue));
  };

  const handlePageChange = (pageNumber) => {
    dispatch(changePage(pageNumber));
  };

  return (
    <div>
      <SearchBar onSearch={handleSearch} searchValue={searchValue} />
      <Filters onFilter={handleFilter} filterValue={filterValue} />
      <SortBy onSort={handleSort} sortValue={sortValue} />
      {error ? (
        <div>Error: {error.message}</div>
      ) : loading ? (
        <div>Loading...</div>
      ) : (
        <div>
          <DogsList dogs={filteredDogs.slice(currentPage * dogsPerPage, (currentPage + 1) * dogsPerPage)} />
          <Pagination
            currentPage={currentPage}
            dogsPerPage={dogsPerPage}
            totalDogs={filteredDogs.length}
            onPageChange={handlePageChange}
          />
        </div>
      )}
      <SortOptions onSort={handleSort} sortValue={sortValue} />
    </div>
  );
};

export default HomePage;
