import React from 'react';
import { useDispatch } from 'react-redux';
import { setFilterType } from '../../redux/actions/index';
import "./filters.css"

const Filters = () => {
  const dispatch = useDispatch();
  const [dataOriginFilter, setDataOriginFilter] = useState('all');

  const handleFilterChange = (event) => {
    dispatch(setFilterTemperament(event.target.value));
  };

  const handleDataOriginChange = (event) => {
    setDataOriginFilter(event.target.value);
    dispatch(setFilterDataOrigin(event.target.value));
  };

  return (
    <div className="filter-container">
      <label className="filter-label" htmlFor="temperament-filter-select">
        Filtrar por temperamento:
      </label>
      <select className="filter-select" id="temperament-filter-select" onChange={handleFilterChange}>
        <option value="all">Todos</option>
        <option value="Grass">Grass</option>
        <option value="Poison">Poison</option>
        <option value="Fire">Fire</option>
        <option value="Flying">Flying</option>
        <option value="Water">Water</option>
        <option value="Bug">Bug</option>
        <option value="Normal">Normal</option>
        <option value="Electric">Electric</option>
        <option value="Ground">Ground</option>
        <option value="Fairy">Fairy</option>
        <option value="Fighting">Fighting</option>
        <option value="Psychic">Psychic</option>
        <option value="Rock">Rock</option>
        <option value="Steel">Steel</option>
        <option value="Ice">Ice</option>
        <option value="Ghost">Ghost</option>
        <option value="Dragon">Dragon</option>
        <option value="Dark">Dark</option>
      </select>
      <label className="filter-label" htmlFor="data-origin-filter-select">
        Filtrar por origen de los datos:
      </label>
      <select className="filter-select" id="data-origin-filter-select" value={dataOriginFilter} onChange={handleDataOriginChange}>
        <option value="all">Todos</option>
        <option value="API">API</option>
        <option value="BDD">BDD</option>
      </select>
    </div>
  );
};

export default Filters;
