import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  findBreeds,
  filterByTemperament,
  filterByOrigin,
  clearFilters,
} from "../../redux/actions";

const SearchBar = () => {
  const dispatch = useDispatch();
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedTemperament, setSelectedTemperament] = useState("");
  const [selectedOrigin, setSelectedOrigin] = useState("");

  const allTemperaments = useSelector(
    (state) => state.dogsReducer.allTemperaments
  );
  const filteredByTemperament = useSelector(
    (state) => state.dogsReducer.filteredByTemperament
  );
  const filteredByOrigin = useSelector(
    (state) => state.dogsReducer.filteredByOrigin
  );

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    dispatch(findBreeds(searchTerm.trim()));
    setSearchTerm("");
  };

  return (
    <div className="search-bar">
      <input
        className="input-search"
        type="text"
        placeholder="Search by breed name..."
        value={searchTerm}
        onChange={handleChange}
      />

      {searchTerm && (
        <button
          type="reset"
          className="clear-button"
          onClick={() => setSearchTerm("")}
        >
          x
        </button>
      )}

      <button type="search" className="search-button" onClick={handleSearch}>
        Search
      </button>
    </div>
  );
};

export default SearchBar;
