import {
  FIND_BREEDS_BY_NAME,
  GET_BREED_DETAIL,
  GET_ALL_BREEDS,
  GET_ALL_TEMPERAMENTS,
  FILTER_BY_TEMPERAMENT,
  FILTER_BY_ORIGIN,
  SORT_BREEDS_BY_NAME,
  SORT_BREEDS_BY_WEIGHT,
  CHANGE_PAGE,
  CLEAR_SEARCH,
  CLEAR_FILTERS,
} from "../../redux/actions/index";

const initialState = {
  breeds: [],
  breedDetail: {},
  allBreeds: [],
  allTemperaments: [],
  filteredBreeds: [],
  searchBreeds: [],
  temperamentsFilter: [],
  originFilter: "",
  order: "",
  currentPage: 1,
  breedsPerPage: 8,
};

const dogsReducer = (state = initialState, action) => {
  switch (action.type) {
    case FIND_BREEDS_BY_NAME:
      return {
        ...state,
        searchBreeds: action.payload,
      };
    case GET_BREED_DETAIL:
      return {
        ...state,
        breedDetail: action.payload,
      };
    case GET_ALL_BREEDS:
      return {
        ...state,
        allBreeds: action.payload,
        breeds: action.payload.slice(0, state.breedsPerPage),
      };
    case GET_ALL_TEMPERAMENTS:
      return {
        ...state,
        allTemperaments: action.payload,
      };
    case FILTER_BY_TEMPERAMENT:
      return {
        ...state,
        temperamentsFilter: action.payload,
      };
    case FILTER_BY_ORIGIN:
      return {
        ...state,
        originFilter: action.payload,
      };
    case SORT_BREEDS_BY_NAME:
      return {
        ...state,
        breeds: action.payload,
        order: "name",
      };
    case SORT_BREEDS_BY_WEIGHT:
      return {
        ...state,
        breeds: action.payload,
        order: "weight",
      };
    case CHANGE_PAGE:
      return {
        ...state,
        currentPage: action.payload,
      };
    case CLEAR_SEARCH:
      return {
        ...state,
        searchBreeds: [],
      };
    case CLEAR_FILTERS:
      return {
        ...state,
        temperamentsFilter: [],
        originFilter: "",
        order: "",
      };
    default:
      return state;
  }
};

export default dogsReducer;
