import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { filterByOrigin, filterByTemperament, clearFilters } from '../../redux/actions';

const Filters = () => {
  const dispatch = useDispatch();
  const [selectedOrigin, setSelectedOrigin] = useState('');
  const [selectedTemperament, setSelectedTemperament] = useState('');

  const handleOriginChange = (event) => {
    setSelectedOrigin(event.target.value);
  };

  const handleTemperamentChange = (event) => {
    setSelectedTemperament(event.target.value);
  };

  const handleOriginFilter = () => {
    if (selectedOrigin) {
      dispatch(filterByOrigin(selectedOrigin));
    } else {
      dispatch(clearFilters());
    }
  };

  const handleTemperamentFilter = () => {
    if (selectedTemperament) {
      dispatch(filterByTemperament(selectedTemperament));
    } else {
      dispatch(clearFilters());
    }
  };

  const handleClearFilters = () => {
    setSelectedOrigin('');
    setSelectedTemperament('');
    dispatch(clearFilters());
  };

  return (
    <div className="filters-container">
      <h3>Filters</h3>
      <div className="filter-section">
        <h4>Origin</h4>
        <div className="radio-container">
          <label>
            <input type="radio" value="api" checked={selectedOrigin === 'api'} onChange={handleOriginChange} />
            API
          </label>
          <label>
            <input type="radio" value="db" checked={selectedOrigin === 'db'} onChange={handleOriginChange} />
            Database
          </label>
        </div>
        <button className="filter-button" onClick={handleOriginFilter}>
          Apply Origin Filter
        </button>
      </div>
      <div className="filter-section">
        <h4>Temperament</h4>
        <select value={selectedTemperament} onChange={handleTemperamentChange}>
          <option value="">Select a temperament</option>
          {/* Mapear los temperamentos disponibles */}
          {temperaments.map((temp) => (
            <option key={temp} value={temp}>
              {temp}
            </option>
          ))}
        </select>
        <button className="filter-button" onClick={handleTemperamentFilter}>
          Apply Temperament Filter
        </button>
      </div>
      <button className="clear-filters-button" onClick={handleClearFilters}>
        Clear Filters
      </button>
    </div>
  );
};

export default Filters;
