import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { loadDogs, changePage } from '../../redux/actions/index';
import DogCard from '../dogCard/DogCard';
import Pagination from '../pagination/Pagination';

const DogsList = ({dogsList}) => {
  const dispatch = useDispatch();
  const dogs = useSelector(state => state.dogs);
  const { currentPage, dogsPerPage, filteredDogs } = dogs;

  useEffect(() => {
    dispatch(loadDogs());
  }, [dispatch]);

  const handleClick = page => {
    dispatch(changePage(page));
  };

  const indexOfLastDog = currentPage * dogsPerPage;
  const indexOfFirstDog = indexOfLastDog - dogsPerPage;
  const currentDogs = dogsList?.slice(indexOfFirstDog, indexOfLastDog) || filteredDogs.slice(indexOfFirstDog, indexOfLastDog);


  return (
    <div className="dogs-list">
      {currentDogs.map(dog => (
        <DogCard key={dog.id} dog={dog} />
      ))}
      <Pagination
        itemsPerPage={dogsPerPage}
        totalItems={filteredDogs.length}
        currentPage={currentPage}
        onPageChange={handleClick}
      />
    </div>
  );
};

export default DogsList;
