import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import DogCard from "../dogCard/DogCard";

const DogsList = ({ sorting }) => {
  const breeds = useSelector((state) => state.breeds);
  const [sortedBreeds, setSortedBreeds] = useState(breeds);

  useEffect(() => {
    if (sorting === "name_asc") {
      setSortedBreeds([...breeds].sort((a, b) => a.name.localeCompare(b.name)));
    } else if (sorting === "name_desc") {
      setSortedBreeds([...breeds].sort((a, b) => b.name.localeCompare(a.name)));
    } else if (sorting === "weight_asc") {
      setSortedBreeds([...breeds].sort((a, b) => a.weight - b.weight));
    } else if (sorting === "weight_desc") {
      setSortedBreeds([...breeds].sort((a, b) => b.weight - a.weight));
    } else {
      setSortedBreeds(breeds);
    }
  }, [sorting, breeds]);

  return (
    <div className="dogs-list-container">
      {sortedBreeds.map((breed) => (
        <DogCard key={breed.id} breed={breed} />
      ))}
    </div>
  );
};

export default DogsList;
