import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { sortBreedsByName, sortBreedsByWeight } from "../../redux/actions";

const Sorting = () => {
  const dispatch = useDispatch();
  const [order, setOrder] = useState("");

  const handleNameSort = () => {
    if (order === "asc" || order === "") {
      dispatch(sortBreedsByName("desc"));
      setOrder("desc");
    } else {
      dispatch(sortBreedsByName("asc"));
      setOrder("asc");
    }
  };

  const handleWeightSort = () => {
    if (order === "asc" || order === "") {
      dispatch(sortBreedsByWeight("desc"));
      setOrder("desc");
    } else {
      dispatch(sortBreedsByWeight("asc"));
      setOrder("asc");
    }
  };

  return (
    <div className="sorting">
      <label>Sort by:</label>
      <button onClick={handleNameSort}>Name</button>
      <button onClick={handleWeightSort}>Weight</button>
    </div>
  );
};

export default Sorting;
