import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { loadDogs, searchDogsByName, filterDogs, sortDogs, setSort, changePage } from '../../redux/actions/index';
import DogsList from '../dogsList/DogsList';
import Filters from '../filters/Filters';
import Pagination from '../pagination/Pagination';
import SearchBar from '../searchBar/SearchBar';
import SortBy from '../sortBy/SortBy';
import SortOptions from '../sortOptions/SortOptions';

const HomePage = () => {
  const dispatch = useDispatch();
  const dogs = useSelector((state) => state.dogs);
  const { loading, error, filteredDogs, currentPage, dogsPerPage, searchValue, filterValue, sortValue } = dogs;

  useEffect(() => {
    dispatch(loadDogs());
  }, [dispatch]);

  const handleSearch = (searchValue) => {
    dispatch(searchDogsByName(searchValue));
  };

  const handleFilter = (filterValue) => {
    dispatch(filterDogs(filterValue));
  };

  const handleSort = (sortValue) => {
    dispatch(setSort(sortValue));
    dispatch(sortDogs(sortValue));
  };

  const handlePageChange = (pageNumber) => {
    dispatch(changePage(pageNumber));
  };

  return (
    <div className='home-page'>
      <h2>Hola</h2>
      <DogsList></DogsList>
