import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { changePage } from "../../redux/actions/index";

const Pagination = () => {
  const dispatch = useDispatch();
  const currentPage = useSelector((state) => state.currentPage);
  const breedsPerPage = useSelector((state) => state.breedsPerPage);
  const totalBreeds = useSelector((state) => state.filteredBreeds.length);
  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(totalBreeds / breedsPerPage); i++) {
    pageNumbers.push(i);
  }

  const handlePrevPage = () => {
    if (currentPage > 1) {
      dispatch(changePage(currentPage - 1));
    }
  };

  const handleNextPage = () => {
    if (currentPage < Math.ceil(totalBreeds / breedsPerPage)) {
      dispatch(changePage(currentPage + 1));
    }
  };

  return (
    <nav>
      <ul className="pagination">
        <li className={`page-item ${currentPage === 1 ? "disabled" : ""}`}>
          <button onClick={handlePrevPage} className="page-link">
            Previous
          </button>
        </li>
        {pageNumbers.map((number) => (
          <li
            key={number}
            className={`page-item ${currentPage === number ? "active" : ""}`}
          >
            <button onClick={() => handleClick(number)} className="page-link">
              {number}
            </button>
          </li>
        ))}
        <li
          className={`page-item ${
            currentPage === Math.ceil(totalBreeds / breedsPerPage)
              ? "disabled"
              : ""
          }`}
        >
          <button onClick={handleNextPage} className="page-link">
            Next
          </button>
        </li>
      </ul>
    </nav>
  );
};

export default Pagination;
