import {
  FETCH_DOGS,
  FETCH_DOGS_SUCCESS,
  FETCH_DOGS_FAILURE,
  FILTER_DOGS_BY_TEMPERAMENT,
  FILTER_DOGS_BY_ORIGIN,
  SORT_DOGS_BY_NAME_ASC,
  SORT_DOGS_BY_NAME_DESC,
  SORT_DOGS_BY_WEIGHT_ASC,
  SORT_DOGS_BY_WEIGHT_DESC,
  SET_CURRENT_PAGE,
} from "../actions/index";

const initialState = {
  dogs: [],
  filteredDogs: [],
  temperamentFilter: "",
  originFilter: "",
  sort: "",
  sortOrder: "",
  currentPage: 1,
  isLoading: false,
  error: "",
};

const dogsReducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_DOGS_REQUEST:
      return {
        ...state,
        isLoading: true,
      };
    case FETCH_DOGS_SUCCESS:
      return {
        ...state,
        isLoading: false,
        dogs: action.payload,
        filteredDogs: action.payload,
      };
    case FETCH_DOGS_FAILURE:
      return {
        ...state,
        isLoading: false,
        error: action.payload,
      };
    case FILTER_DOGS_BY_TEMPERAMENT:
      return {
        ...state,
        temperamentFilter: action.payload,
      };
    case FILTER_DOGS_BY_ORIGIN:
      return {
        ...state,
        originFilter: action.payload,
      };
    case SORT_DOGS_BY_NAME_ASC:
      return {
        ...state,
        sort: "name",
        sortOrder: "asc",
      };
    case SORT_DOGS_BY_NAME_DESC:
      return {
        ...state,
        sort: "name",
        sortOrder: "desc",
      };
    case SORT_DOGS_BY_WEIGHT_ASC:
      return {
        ...state,
        sort: "weight",
        sortOrder: "asc",
      };
    case SORT_DOGS_BY_WEIGHT_DESC:
      return {
        ...state,
        sort: "weight",
        sortOrder: "desc",
      };
    case SET_CURRENT_PAGE:
      return {
        ...state,
        currentPage: action.payload,
      };
    default:
      return state;
  }
};

export default dogsReducer;
