import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { createNewDog } from "../../redux/actions/index";

const FormPage = () => {
  const dispatch = useDispatch();
  const [formData, setFormData] = useState({
    name: "",
    minHeight: "",
    maxHeight: "",
    minWeight: "",
    maxWeight: "",
    life_span: "",
    temperaments: [],
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleTemperamentsChange = (event) => {
    const values = Array.from(
      event.target.selectedOptions,
      (option) => option.value
    );
    setFormData({
      ...formData,
      temperaments: values,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Validaciones
    if (formData.minHeight > formData.maxHeight) {
      alert("La altura mínima no puede ser mayor a la altura máxima");
      return;
    }
    if (formData.minWeight > formData.maxWeight) {
      alert("El peso mínimo no puede ser mayor al peso máximo");
      return;
    }
    if (formData.name.match(/\d/)) {
      alert("El nombre no puede contener números");
      return;
    }
    if (formData.temperaments.length === 0) {
      alert("Debe seleccionar al menos un temperamento");
      return;
    }
    dispatch(createNewDog(formData));
    // Reseteamos el formulario
    setFormData({
      name: "",
      minHeight: "",
      maxHeight: "",
      minWeight: "",
      maxWeight: "",
      life_span: "",
      temperaments: [],
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Aquí agregamos los campos del formulario */}
      <label htmlFor="name">Nombre:</label>
      <input
        type="text"
        id="name"
        name="name"
        value={formData.name}
        onChange={handleInputChange}
        required
      />

      <label htmlFor="minHeight">Altura mínima:</label>
      <input
        type="number"
        id="minHeight"
        name="minHeight"
        value={formData.minHeight}
        onChange={handleInputChange}
        required
      />
      <label htmlFor="maxHeight">Altura máxima:</label>
      <input
        type="number"
        id="maxHeight"
        name="maxHeight"
        value={formData.maxHeight}
        onChange={handleInputChange}
        required
      />

      <label htmlFor="minWeight">Peso mínimo:</label>
      <input
        type="number"
        id="minWeight"
        name="minWeight"
        value={formData.minWeight}
        onChange={handleInputChange}
        required
      />
      <label htmlFor="maxWeight">Peso máximo:</label>
      <input
        type="number"
        id="maxWeight"
        name="maxWeight"
        value={formData.maxWeight}
        onChange={handleInputChange}
        required
      />

      <label htmlFor="life_span">Años de vida:</label>
      <input
        type="number"
        id="life_span"
        name="life_span"
        value={formData.life_span}
        onChange={handleInputChange}
        required
      />

      <label htmlFor="temperaments">Temperamentos:</label>
      <select
        type="text"
        id="temperaments"
        name="temperaments"
        multiple
        value={formData.temperaments}
        onChange={handleTemperamentsChange}
        required
      />
      <button className="createdNewDog" type="submit" onClick={handleSubmit}>
        Create New Dog
      </button>
    </form>
  );
};

export default FormPage;
