import {
  GET_DOGS,
  GET_TEMPERAMENTS,
  FILTER_BY_NAME,
  FILTER_BY_TEMPERAMENT,
  FILTER_BY_ORIGIN,
  SORT_BY_NAME,
  SORT_BY_WEIGHT,
  SET_PAGE_NUMBER,
} from "../actions";

const initialState = {
  dogs: [],
  filteredDogs: [],
  temperaments: [],
  filterByName: "",
  filterByTemperament: "",
  filterByOrigin: "",
  sortBy: "",
  pageNumber: 1,
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_DOGS:
      return {
        ...state,
        dogs: action.payload,
        filteredDogs: action.payload,
      };
    case GET_TEMPERAMENTS:
      return {
        ...state,
        temperaments: action.payload,
      };
    case FILTER_BY_NAME:
      return {
        ...state,
        filterByName: action.payload.toLowerCase(),
      };
    case FILTER_BY_TEMPERAMENT:
      return {
        ...state,
        filterByTemperament: action.payload,
      };
    case FILTER_BY_ORIGIN:
      return {
        ...state,
        filterByOrigin: action.payload,
      };
    case SORT_BY_NAME:
      return {
        ...state,
        sortBy: action.payload,
      };
    case SORT_BY_WEIGHT:
      return {
        ...state,
        sortBy: action.payload,
      };
    case SET_PAGE_NUMBER:
      return {
        ...state,
        pageNumber: action.payload,
      };
    default:
      return state;
  }
};

export default rootReducer;
