import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { changePage } from '../actions';
import { DogsList, DogCard } from './';
import '../styles/Pagination.css';

const Pagination = ({ currentPage, totalPages, dogs, changePage }) => {
  const handleClick = (page) => {
    if (page < 1 || page > totalPages) return;
    changePage(page);
  };

  const renderDogs = () => {
    const startIndex = (currentPage - 1) * 8;
    const endIndex = startIndex + 8;
    const currentDogs = dogs.slice(startIndex, endIndex);
    return currentDogs.map((dog) => <DogCard key={dog.id} dog={dog} />);
  };

  return (
    <div className="pagination">
      <DogsList>{renderDogs()}</DogsList>
      <div className="pagination-buttons">
        <button onClick={() => handleClick(currentPage - 1)} disabled={currentPage === 1}>
          Previous
        </button>
        <p>
          Page {currentPage} of {totalPages}
        </p>
        <button onClick={() => handleClick(currentPage + 1)} disabled={currentPage === totalPages}>
          Next
        </button>
      </div>
    </div>
  );
};

Pagination.propTypes = {
  currentPage: PropTypes.number.isRequired,
  totalPages: PropTypes.number.isRequired,
  dogs: PropTypes.array.isRequired,
  changePage: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  currentPage: state.currentPage,
  totalPages: state.totalPages,
  dogs: state.displayedDogs,
});

export default connect(mapStateToProps, { changePage })(Pagination);
