import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchDogs, filterByTemperament, filterByOrigin, orderBy, searchByName } from "../actions";
import DogCard from "./DogCard";
import Filters from "./Filters";
import Pagination from "./Pagination";
import SearchBar from "./SearchBar";
import SortBy from "./SortBy";

const HomePage = () => {
  const [currentPage, setCurrentPage] = useState(1);
  const [dogsPerPage] = useState(8);
  const dispatch = useDispatch();
  const { dogs, loading, error } = useSelector((state) => state.dogs);

  useEffect(() => {
    dispatch(fetchDogs());
  }, [dispatch]);

  const indexOfLastDog = currentPage * dogsPerPage;
  const indexOfFirstDog = indexOfLastDog - dogsPerPage;
  const currentDogs = dogs.slice(indexOfFirstDog, indexOfLastDog);

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const handleTemperamentFilter = (event) => {
    const temperament = event.target.value;
    dispatch(filterByTemperament(temperament));
  };

  const handleOriginFilter = (event) => {
    const origin = event.target.value;
    dispatch(filterByOrigin(origin));
  };

  const handleSortBy = (event) => {
    const orderByOption = event.target.value;
    dispatch(orderBy(orderByOption));
  };

  const handleSearch = (event) => {
    const name = event.target.value;
    dispatch(searchByName(name));
  };

  return (
    <div>
      <SearchBar handleSearch={handleSearch} />
      <Filters handleTemperamentFilter={handleTemperamentFilter} handleOriginFilter={handleOriginFilter} />
      <SortBy handleSortBy={handleSortBy} />
      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <div>
          {currentDogs.map((dog) => (
            <DogCard key={dog.id} dog={dog} />
          ))}
        </div>
      )}
      <Pagination dogsPerPage={dogsPerPage} totalDogs={dogs.length} paginate={paginate} />
    </div>
  );
};

export default HomePage;
