import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { filterDogs, loadDogs, setSort } from '../../redux/actions';
import SortBy from '../sortBy/SortBy';

const Filters = () => {
  const dispatch = useDispatch();
  const { temperaments } = useSelector(state => state.dogs);
  const [selectedTemperament, setSelectedTemperament] = useState('');
  const [selectedOrigin, setSelectedOrigin] = useState('');
  const [sortBy, setSortBy] = useState({ label: 'Nombre', value: 'name' });

  const handleTemperamentFilter = (e) => {
    const selectedValue = e.target.value;
    setSelectedTemperament(selectedValue);
    dispatch(filterDogs(selectedValue, selectedOrigin));
  };

  const handleOriginFilter = (e) => {
    const selectedValue = e.target.value;
    setSelectedOrigin(selectedValue);
    dispatch(filterDogs(selectedTemperament, selectedValue));
  };

  const handleSortBy = (selectedSortBy) => {
    setSortBy(selectedSortBy);
    dispatch(setSort(selectedSortBy.value));
  };

  const handleResetFilters = () => {
    setSelectedTemperament('');
    setSelectedOrigin('');
    dispatch(loadDogs());
  };

  return (
    <div className="filters-container">
      <div className="filters-section">
        <span className="filters-label">Filtrar por:</span>
        <select className="filter-select" value={selectedTemperament} onChange={handleTemperamentFilter}>
          <option value="">Temperamento</option>
          {temperaments && temperaments.map(temp => <option key={temp.id} value={temp.name}>{temp.name}</option>)}
        </select>
        <select className="filter-select" value={selectedOrigin} onChange={handleOriginFilter}>
          <option value="">Origen</option>
          <option value="api">API</option>
          <option value="db">Base de datos</option>
        </select>
        <button className="reset-filters-btn" onClick={handleResetFilters}>Resetear Filtros</button>
      </div>
      <div className="sort-section">
        <span className="sort-label">Ordenar por:</span>
        <SortBy selected={sortBy} handleSortBy={handleSortBy} />
      </div>
    </div>
  );
};

export default Filters;
