import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { findBreedsByName } from "../../redux/actions";

const SearchBar = () => {
  const dispatch = useDispatch();
  const searchResults = useSelector((state) => state.searchResults);
  const [searchTerm, setSearchTerm] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    setErrorMessage("");
    await dispatch(findBreedsByName(searchTerm.trim()));
    if (!searchResults) {
      setErrorMessage("No se encontró ningún perro con el nombre ingresado");
    }
    setSearchTerm("");
  };

  const handleClear = () => {
    setSearchTerm("");
  };

  return (
    <div className="searchBar-container">
      <form className="form-search" onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Search by name..."
          value={searchTerm}
          onChange={handleChange}
        />

        {searchTerm && (
          <button type="reset" className="clear-button" onClick={handleClear}>
            x
          </button>
        )}

        <button type="submit" className="search-button">
          Search
        </button>
      </form>
      {errorMessage && <h2>{errorMessage}</h2>}
    </div>
  );
};

export default SearchBar;
