import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { changePage } from "../../redux/actions/index";
import "./pagination.css";

const Pagination = () => {
  const dispatch = useDispatch();

  const breedsPerPage = useSelector((state) => state.breedsPerPage);
  const breeds = useSelector((state) => state.breeds);
  const filterTemperament = useSelector((state) => state.filteredByTemperament);
  const filterOrigin = useSelector((state) => state.filteredByOrigin);

  const [pageNumbers, setPageNumbers] = useState([]);

  const handleClick = (pageNumber) => {
    dispatch(changePage(pageNumber));
  };

  useEffect(() => {
    const totalPages =
      filterTemperament.length > 0
        ? Math.ceil(filterTemperament.length / breedsPerPage)
        : Math.ceil(breeds.length / breedsPerPage);
    setPageNumbers(Array.from(Array(totalPages).keys()));
  }, [breeds, filterTemperament, breedsPerPage]);

  const rangeStart =
    (useSelector((state) => state.currentPage) - 1) * breedsPerPage;
  const rangeEnd = rangeStart + breedsPerPage;
  const visiblePageNumbers = pageNumbers.filter((number) => {
    const pageNumberStart = number * breedsPerPage;
    const pageNumberEnd = pageNumberStart + breedsPerPage;
    return pageNumberStart <= rangeEnd && pageNumberEnd > rangeStart;
  });

  return (
    <nav className="pagination-bar">
      <div className="pagination">
        {pageNumbers.map((number) => (
          <button
            key={number}
            onClick={() => handleClick(number + 1)}
            className="page-button"
          >
            {number + 1}
          </button>
        ))}
      </div>
    </nav>
  );
};

export default Pagination;
