export const FETCH_DOGS = "FETCH_DOGS";
export const FETCH_DOGS_SUCCESS = "FETCH_DOGS_SUCCESS";
export const FETCH_DOGS_FAILURE = "FETCH_DOGS_FAILURE";
export const FILTER_DOGS_BY_TEMPERAMENT = "FILTER_DOGS_BY_TEMPERAMENT";
export const FILTER_DOGS_BY_ORIGIN = "FILTER_DOGS_BY_ORIGIN";
export const SORT_DOGS_BY_NAME_ASC = "SORT_DOGS_BY_NAME_ASC";
export const SORT_DOGS_BY_NAME_DESC = "SORT_DOGS_BY_NAME_DESC";
export const SORT_DOGS_BY_WEIGHT_ASC = "SORT_DOGS_BY_WEIGHT_ASC";
export const SORT_DOGS_BY_WEIGHT_DESC = "SORT_DOGS_BY_WEIGHT_DESC";
export const SET_CURRENT_PAGE = "SET_CURRENT_PAGE";
export const SET_SORT = "SET_SORT";
export const SET_SORT_ORDER = "SET_SORT_ORDER";
export const SET_FILTER_TYPE = "SET_FILTER_TYPE";

