import {
  GET_DOGS_SUCCESS,
  GET_DOGS_BY_ORIGIN,
  FILTER_BY_TEMPERAMENT,
  FILTER_BY_TEMPERAMENT_CLEAR,
  FILTER_BY_ORIGIN_CLEAR,
  SORT_BREEDS_BY_NAME,
  SORT_BREEDS_BY_WEIGHT,
  CHANGE_PAGE,
  CLEAR_SEARCH,
} from "../../redux/actions/index";

const initialState = {
  dogs: [],
  filteredDogs: [],
  breeds: [],
  filteredBreeds: [],
  searchResults: [],
  currentPage: 1,
  breedsPerPage: 8,
};

const dogsReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_DOGS_SUCCESS:
      return {
        ...state,
        dogs: action.payload,
        filteredDogs: action.payload,
      };
    case GET_DOGS_BY_ORIGIN:
      return {
        ...state,
        filteredDogs: action.payload,
      };
    case FILTER_BY_TEMPERAMENT:
      const filteredBreeds = state.breeds.filter((breed) =>
        breed.temperament?.includes(action.payload)
      );
      return {
        ...state,
        filteredBreeds: filteredBreeds,
      };
    case FILTER_BY_TEMPERAMENT_CLEAR:
      return {
        ...state,
        filteredBreeds: state.breeds,
      };
    case FILTER_BY_ORIGIN_CLEAR:
      return {
        ...state,
        filteredDogs: state.dogs,
      };
    case SORT_BREEDS_BY_NAME:
      return {
        ...state,
        filteredBreeds: action.payload,
      };
    case SORT_BREEDS_BY_WEIGHT:
      return {
        ...state,
        filteredBreeds: action.payload,
      };
    case CHANGE_PAGE:
      return {
        ...state,
        currentPage: action.payload,
      };
    case CLEAR_SEARCH:
      return {
        ...state,
        searchResults: [],
      };
    default:
      return state;
  }
};

export default dogsReducer;
