import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getDogs, filterByTemperament, orderBy, filterBySource, changePage } from "../../redux/actions";
import DogCard from "../components/DogCard";
import SearchBar from "../components/SearchBar";
import FilterSelect from "../components/FilterSelect";
import OrderSelect from "../components/OrderSelect";
import Pagination from "../components/Pagination";
import "./homePage.css";

const HomePage = () => {
  const dispatch = useDispatch();

  const {
    dogs,
    temperaments,
    filteredByTemp,
    orderedBy,
    filteredBySource,
    currentPage,
    dogsPerPage,
  } = useSelector((state) => state);

  useEffect(() => {
    dispatch(getDogs());
  }, [dispatch]);

  const handleFilterByTemp = (event) => {
    dispatch(filterByTemperament(event.target.value));
  };

  const handleOrderBy = (event) => {
    dispatch(orderBy(event.target.value));
  };

  const handleFilterBySource = (event) => {
    dispatch(filterBySource(event.target.value));
  };

  const handlePageChange = (pageNumber) => {
    dispatch(changePage(pageNumber));
  };

  const indexOfLastDog = currentPage * dogsPerPage;
  const indexOfFirstDog = indexOfLastDog - dogsPerPage;
  const currentDogs = filteredByTemp
    .filter((dog) => filteredBySource === "all" || dog.createdInDb === filteredBySource)
    .sort((a, b) =>
      orderedBy === "weight"
        ? a.weight.split(" - ")[0] - b.weight.split(" - ")[0]
        : a.name.localeCompare(b.name)
    )
    .slice(indexOfFirstDog, indexOfLastDog);

  const renderDogs = () => {
    if (dogs.length === 0) {
      return <p>Loading...</p>;
    } else if (filteredByTemp.length === 0) {
      return <p>No results found.</p>;
    } else {
      return currentDogs.map((dog) => <DogCard key={dog.id} dog={dog} />);
    }
  };

  return (
    <div className="home-container">
      <SearchBar />
      <div className="filter-container">
        <FilterSelect
          options={temperaments.map((temp) => temp.name)}
          handleChange={handleFilterByTemp}
          defaultOption="Filter by temperament"
        />
        <FilterSelect
          options={["all", "api", "db"]}
          handleChange={handleFilterBySource}
          defaultOption="Filter by source"
        />
      </div>
      <div className="order-container">
        <OrderSelect handleChange={handleOrderBy} defaultOption="Order by" />
      </div>
      <div className="dogs-container">{renderDogs()}</div>
      <Pagination
        dogsPerPage={dogsPerPage}
        totalDogs={filteredByTemp.length}
        currentPage={currentPage}
        onPageChange={handlePageChange}
      />
    </div>
  );
};

export default HomePage;
