export const LOAD_DOGS = "LOAD_DOGS";
export const FILTER_DOGS = "FILTER_DOGS";
export const SORT_DOGS = "SORT_DOGS";
export const SET_SORT = "SET_SORT";
export const GET_DOG_DETAIL = "GET_DOG_DETAIL";
export const CHANGE_PAGE = "CHANGE_PAGE";

import axios from "axios";

// Acción para buscar razas de perros por nombre
export const searchDogsByName = (name) => {
  return async (dispatch) => {
    try {
      if (name) {
        const res = await axios.get(
          `http://localhost:3001/dogs/name?name=${name}`
        );
        dispatch({ type: SEARCH_DOGS_BY_NAME, payload: res.data });
      }
    } catch (error) {
      console.log(error);
    }
  };
};

// Acción para cargar los primeros resultados de razas de perros
export const loadDogs = () => {
  return async (dispatch) => {
    try {
      const res = await axios.get("http://localhost:3001/dogs");
      dispatch({ type: LOAD_DOGS, payload: res.data });
    } catch (error) {
      console.log(error);
    }
  };
};

// Acción para filtrar razas de perros por temperamento y origen
export const filterDogs = (filters) => {
  return async (dispatch) => {
    try {
      const res = await axios.get(
        `http://localhost:3001/dogs?temperaments=${filters.temperament}&origin=${filters.origin}`
      );
      dispatch({ type: FILTER_DOGS, payload: res.data });
    } catch (error) {
      console.log(error);
    }
  };
};

// Acción para ordenar razas de perros alfabéticamente y por peso
export const sortDogs = ({ sortType, origin }) => {
  return async (dispatch) => {
    try {
      let res;
      if (origin) {
        res = await axios.get(
          `http://localhost:3001/dogs?sort=${sortType}&origin=${origin}`
        );
      } else {
        res = await axios.get(`http://localhost:3001/dogs?sort=${sortType}`);
      }
      dispatch({ type: SORT_DOGS, payload: res.data });
    } catch (error) {
      console.log(error);
    }
  };
};

// Acción para para cambiar el tipo de clasificación actual de la lista de razas de perros.
export const setSort = (sort) => ({
  type: SET_SORT,
  payload: sort,
});

// Acción para obtener el detalle de una raza de perro específica
export const getDogDetail = (id) => {
  return async (dispatch) => {
    try {
      const res = await axios.get(`http://localhost:3001/dogs/id/${id}`);
      dispatch({ type: GET_DOG_DETAIL, payload: res.data });
    } catch (error) {
      console.log(error);
    }
  };
};

// Acción para cambiar la página actual del paginado
export const changePage = (page) => {
  return { type: CHANGE_PAGE, payload: page };
};
